<template>
    <div>
        talk page
        <input type="text" v-model='msg'> 
        <button  @click="submit">发送</button>
        <div v-for= '(msg1,index) in CHAT' :key= "index" style="    font-size: 17px;
    margin: 2vh 0;">
            <span>消息：{{msg1.msg}}</span>
            <span>来自：{{msg1.fromUser}}</span>
            <span>时间:{{msg1.time}}</span>
        </div>
    </div>
</template>

<script>
import CHAT from '../lib/client.js'
import { mapGetters, mapActions } from 'vuex'

export default {
    data(){
        return {
            msg:'',
            CHAT:CHAT.msgArr,
            username: ''
        }
    },
    mounted(){
        console.log(this.user.username)
        CHAT.init(this.user.username)
        CHAT.message(this.user.username)
        console.log(CHAT.msgArr)
    },
    methods:{
        submit(){
            var date = new Date()
            var time = date.getHours() + ':' + date.getMinutes()
            var obj = {
                time: time,
                msg: this.msg,
                toUser:'群聊',
                fromUser: this.user.username
            }
            if(obj.toUser != '群聊') {
                var user = {
                time: time,
                lastMsg: this.msg,
                username: this.$route.query.username,
                url: this.$route.query.url
                }
            } else {
                var user = {
                    time: time,
                    lastMsg: this.msg,
                    username: this.$route.query.username,
                    url: 'data:image/png;base64,'
                }
            }
                // this.addUserList(user)
                // this.msg = ''
                CHAT.submit(obj)
             }
    },

    computed:{
        ...mapGetters(['user'])
    }
}
</script>

